--
-- Table structure for table `articulo`
--

CREATE TABLE IF NOT EXISTS `articulo` (
  `id` int(11) NOT NULL,
  `id_estatus` int(11) NOT NULL,
  `descripcion` longtext COLLATE utf8_unicode_ci NOT NULL,
  `tipoarticulo` longtext COLLATE utf8_unicode_ci NOT NULL,
  `seccionarticulo` longtext COLLATE utf8_unicode_ci
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

INSERT INTO `articulo` (`id`, `id_estatus`, `descripcion`, `tipoarticulo`, `seccionarticulo`) VALUES
  (1, 1, '<p>Nuestros paquetes est&aacute;n dise&ntilde;ados con la finalidad de que Usted pueda planear enteramente su viaje, nuestra selecci&oacute;n de tours y excursiones en cada paquete le ayuda a maximizar su tiempo y experiencia con los atractivos que tenemos para ofrecerle de acuerdo, al n&uacute;mero de noches de estancia en M&eacute;rida y el estado de Yucat&aacute;n.</p>\r\n<p>As&iacute; mismo le presentamos una variedad de hoteles de distintas categor&iacute;as para que escoja el que m&aacute;s se adapte a su gusto y presupuesto.</p>', 'pagina', 'paquete'),
  (2, 1, '<p>&iquest;Cuantas veces se puede decir, &ldquo;conozco aquel lugar&rdquo; en lugar de &ldquo;ya estuve ah&iacute;&rdquo;? Estos Tours y paquetes que ofrecemos, Est&aacute;n dise&ntilde;ados para que usted <span style="text-decoration: underline;">conozca </span>nuestra cultura, coma nuestra comida, conviva con nuestra gente, y que se vea usted envuelto en un velo de magia, que se convertir&aacute; en una experiencia &uacute;nica. Nuestro prop&oacute;sito es que usted sienta la presencia de los Dioses mayas, y viva la naturaleza de este hermoso estado como nunca lo pens&oacute; posible&hellip; No queremos que cuando le pregunten acerca de Yucat&aacute;n diga que &ldquo;Esta bonito,&rdquo; queremos que despu&eacute;s de vivir esta experiencia, usted vuelva a su hogar, con un nuevo sabor de boca, y que cuando le pregunten acerca de su experiencia, el coraz&oacute;n se exprese solo. Estos Tour est&aacute;n dise&ntilde;ados con ese objetivo en mente, ya que si decide deleitarnos con su presencia en Yucat&aacute;n, prep&aacute;rese para relajarse y disfrutar de la mejor experiencia tur&iacute;stica del sureste de M&eacute;xico.</p>', 'pagina', 'tour'),
  (3, 1, '<p>Nuestra selecci&oacute;n de hoteles, convenientemente ubicados en esta hermosa ciudad, le ofrece diferentes categor&iacute;as y&nbsp; est&aacute;ndares con un precio asequible a cada presupuesto.</p>\r\n<p>Estamos seguros que hallara el hotel que busca para su estancia</p>', 'pagina', 'hotel'),
  (4, 1, 'Actualidad', 'peninsula', NULL),
  (5, 1, 'Descubrimiento', 'peninsula', NULL),
  (6, 1, 'El Inicio', 'peninsula', NULL),
  (7, 1, 'Epoca Colonial', 'peninsula', NULL),
  (8, 1, 'Guerra de Castas', 'peninsula', NULL),
  (9, 1, 'Jacinto Canek', 'peninsula', NULL),
  (10, 1, 'Origen del Hombre', 'peninsula', NULL),
  (11, 1, 'Pirateria en Yucatan', 'peninsula', NULL),
  (12, 1, 'Republica de Yucatan', 'peninsula', NULL),
  (13, 1, 'Separacion de Campeche', 'peninsula', NULL),
  (14, 1, 'Yucatan en el Mexico Independiente', 'peninsula', NULL);

ALTER TABLE `articulo`
ADD PRIMARY KEY (`id`),
ADD KEY `IDX_69E94E9150BDD1F3` (`id_estatus`);

ALTER TABLE `articulo`
MODIFY `id` int(11) NOT NULL AUTO_INCREMENT,AUTO_INCREMENT=15;

ALTER TABLE `articulo`
